contracts/DutchAuctionEVM.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

interface ISUIEscrow {
    function revealAndRelease(string calldata secret, address beneficiary) external;
    function canReveal() external view returns (bool);
    function getStatus() external view returns (bool, bool, bool, address, uint256);
}

/**
 * @title DutchAuctionEVM
 * @dev Dutch auction that integrates with SUIEscrow contracts
 */
contract DutchAuctionEVM is ReentrancyGuard, Ownable {
    IERC20 public immutable usdc;

    struct Auction {
        address escrowContract;
        address seller;
        uint256 startPrice;
        uint256 endPrice;
        uint256 startTime;
        uint256 duration;
        string secret; // Stored after auction ends
        bytes32 secretHash;
        bool isActive;
        bool isEnded;
        address winner;
        uint256 finalPrice;
        string metadata;
    }

    mapping(uint256 => Auction) public auctions;
    mapping(address => uint256[]) public userAuctions; // seller -> auction IDs
    mapping(address => uint256[]) public userBids; // bidder -> auction IDs they participated in

    uint256 public nextAuctionId;
    uint256 public protocolFeePercent = 250; // 2.5%
    uint256 public constant MAX_FEE = 1000; // Max 10%

    event AuctionCreated(
        uint256 indexed auctionId,
        address indexed seller,
        address indexed escrowContract,
        uint256 startPrice,
        uint256 endPrice,
        uint256 duration
    );

    event BidPlaced(
        uint256 indexed auctionId,
        address indexed bidder,
        uint256 price,
        uint256 timestamp
    );

    event AuctionEnded(
        uint256 indexed auctionId,
        address indexed winner,
        uint256 finalPrice
    );

    event EscrowReleased(
        uint256 indexed auctionId,
        address indexed escrow,
        address indexed beneficiary
    );

    modifier validAuction(uint256 auctionId) {
        require(auctionId < nextAuctionId, "Auction does not exist");
        _;
    }

    modifier auctionActive(uint256 auctionId) {
        require(auctions[auctionId].isActive, "Auction not active");
        require(!auctions[auctionId].isEnded, "Auction already ended");
        require(block.timestamp < auctions[auctionId].startTime + auctions[auctionId].duration, "Auction expired");
        _;
    }

    constructor(address _usdc) Ownable(msg.sender) ReentrancyGuard() {
        require(_usdc != address(0), "Invalid USDC address");
        usdc = IERC20(_usdc);
    }

    /**
     * @notice Create a Dutch auction linked to an escrow contract
     * @param escrowContract Address of the deployed SUIEscrow contract
     * @param startPrice Starting price in USDC (6 decimals)
     * @param endPrice Ending price in USDC (6 decimals) 
     * @param duration Auction duration in seconds
     * @param secretHash Hash of the secret (must match escrow)
     * @param metadata Additional auction metadata
     */
    function createAuction(
        address escrowContract,
        uint256 startPrice,
        uint256 endPrice,
        uint256 duration,
        bytes32 secretHash,
        string calldata metadata
    ) external returns (uint256) {
        require(escrowContract != address(0), "Invalid escrow contract");
        require(startPrice > endPrice, "Invalid price range");
        require(endPrice > 0, "End price must be > 0");
        require(duration > 0 && duration <= 7 days, "Invalid duration");
        require(secretHash != bytes32(0), "Invalid secret hash");

        // Verify the escrow contract can be revealed
        ISUIEscrow escrow = ISUIEscrow(escrowContract);
        require(escrow.canReveal(), "Escrow cannot be revealed");

        uint256 auctionId = nextAuctionId++;

        auctions[auctionId] = Auction({
            escrowContract: escrowContract,
            seller: msg.sender,
            startPrice: startPrice,
            endPrice: endPrice,
            startTime: block.timestamp,
            duration: duration,
            secret: "",
            secretHash: secretHash,
            isActive: true,
            isEnded: false,
            winner: address(0),
            finalPrice: 0,
            metadata: metadata
        });

        userAuctions[msg.sender].push(auctionId);

        emit AuctionCreated(
            auctionId,
            msg.sender,
            escrowContract,
            startPrice,
            endPrice,
            duration
        );

        return auctionId;
    }

    /**
     * @notice Place a bid in the Dutch auction
     * @param auctionId The auction to bid on
     */
    function placeBid(uint256 auctionId) 
        external 
        nonReentrant 
        validAuction(auctionId) 
        auctionActive(auctionId) 
    {
        Auction storage auction = auctions[auctionId];
        require(msg.sender != auction.seller, "Seller cannot bid");

        uint256 currentPrice = getCurrentPrice(auctionId);
        uint256 protocolFee = (currentPrice * protocolFeePercent) / 10000;
        uint256 totalRequired = currentPrice + protocolFee;

        // Transfer USDC from bidder
        require(
            usdc.transferFrom(msg.sender, address(this), totalRequired),
            "USDC transfer failed"
        );

        // End the auction immediately (Dutch auction - first bid wins)
        auction.isEnded = true;
        auction.isActive = false;
        auction.winner = msg.sender;
        auction.finalPrice = currentPrice;

        userBids[msg.sender].push(auctionId);

        emit BidPlaced(auctionId, msg.sender, currentPrice, block.timestamp);
        emit AuctionEnded(auctionId, msg.sender, currentPrice);

        // Transfer payment to seller (minus protocol fee)
        require(
            usdc.transfer(auction.seller, currentPrice),
            "Payment to seller failed"
        );
    }

    /**
     * @notice Reveal secret and release escrow to auction winner
     * @param auctionId The auction ID
     * @param secret The secret that matches secretHash
     */
    function releaseEscrow(uint256 auctionId, string calldata secret) 
        external 
        nonReentrant 
        validAuction(auctionId) 
    {
        Auction storage auction = auctions[auctionId];
        require(auction.isEnded, "Auction not ended");
        require(auction.winner != address(0), "No winner");
        require(
            msg.sender == auction.seller || msg.sender == owner(),
            "Only seller or owner can release"
        );

        // Verify secret hash
        bytes32 computedHash = keccak256(abi.encodePacked(secret));
        require(computedHash == auction.secretHash, "Invalid secret");

        // Store secret
        auction.secret = secret;

        // Release escrow to winner
        ISUIEscrow escrow = ISUIEscrow(auction.escrowContract);
        escrow.revealAndRelease(secret, auction.winner);

        emit EscrowReleased(auctionId, auction.escrowContract, auction.winner);
    }

    /**
     * @notice End auction if time expired without bids
     */
    function endExpiredAuction(uint256 auctionId) 
        external 
        validAuction(auctionId) 
    {
        Auction storage auction = auctions[auctionId];
        require(auction.isActive, "Auction not active");
        require(
            block.timestamp >= auction.startTime + auction.duration,
            "Auction not expired"
        );

        auction.isActive = false;
        auction.isEnded = true;

        emit AuctionEnded(auctionId, address(0), 0);
    }

    /**
     * @notice Get current price for an active auction
     */
    function getCurrentPrice(uint256 auctionId) 
        public 
        view 
        validAuction(auctionId) 
        returns (uint256) 
    {
        Auction memory auction = auctions[auctionId];

        if (!auction.isActive || auction.isEnded) {
            return auction.finalPrice;
        }

        uint256 elapsed = block.timestamp - auction.startTime;

        if (elapsed >= auction.duration) {
            return auction.endPrice;
        }

        // Linear price decay
        uint256 priceReduction = (auction.startPrice - auction.endPrice) * elapsed / auction.duration;
        return auction.startPrice - priceReduction;
    }

    /**
     * @notice Check if auction is active and can receive bids
     */
    function isAuctionActive(uint256 auctionId) 
        external 
        view 
        validAuction(auctionId) 
        returns (bool) 
    {
        Auction memory auction = auctions[auctionId];
        return auction.isActive && 
               !auction.isEnded && 
               block.timestamp < auction.startTime + auction.duration;
    }

    /**
     * @notice Get time remaining in auction
     */
    function getTimeRemaining(uint256 auctionId) 
        external 
        view 
        validAuction(auctionId) 
        returns (uint256) 
    {
        Auction memory auction = auctions[auctionId];

        if (!auction.isActive || auction.isEnded) {
            return 0;
        }

        uint256 endTime = auction.startTime + auction.duration;

        if (block.timestamp >= endTime) {
            return 0;
        }

        return endTime - block.timestamp;
    }

    /**
     * @notice Get auction details
     */
    function getAuction(uint256 auctionId) 
        external 
        view 
        validAuction(auctionId) 
        returns (
            address escrowContract,
            address seller,
            uint256 startPrice,
            uint256 endPrice,
            uint256 timeRemaining,
            bool isActive,
            bool isEnded,
            address winner,
            uint256 finalPrice,
            uint256 currentPrice
        ) 
    {
        Auction memory auction = auctions[auctionId];

        escrowContract = auction.escrowContract;
        seller = auction.seller;
        startPrice = auction.startPrice;
        endPrice = auction.endPrice;
        timeRemaining = this.getTimeRemaining(auctionId);
        isActive = auction.isActive;
        isEnded = auction.isEnded;
        winner = auction.winner;
        finalPrice = auction.finalPrice;
        currentPrice = getCurrentPrice(auctionId);
    }

    /**
     * @notice Get user's auctions (as seller)
     */
    function getUserAuctions(address user) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return userAuctions[user];
    }

    /**
     * @notice Get user's bids (as bidder)
     */
    function getUserBids(address user) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return userBids[user];
    }

    /**
     * @notice Update protocol fee (owner only)
     */
    function setProtocolFee(uint256 _newFeePercent) external onlyOwner {
        require(_newFeePercent <= MAX_FEE, "Fee too high");
        protocolFeePercent = _newFeePercent;
    }

    /**
     * @notice Withdraw protocol fees (owner only)
     */
    function withdrawFees(address to) external onlyOwner {
        require(to != address(0), "Invalid address");
        uint256 balance = usdc.balanceOf(address(this));
        require(balance > 0, "No fees to withdraw");

        require(usdc.transfer(to, balance), "Fee withdrawal failed");
    }

    /**
     * @notice Get total auctions count
     */
    function getTotalAuctions() external view returns (uint256) {
        return nextAuctionId;
    }
}

contracts/interfaces/I1inch.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title 1inch Fusion+ Interfaces
 * @dev Custom interfaces for 1inch integration
 */

interface IFusionOrder {
    struct Order {
        uint256 salt;
        address makerAsset;    // Token being sold
        address takerAsset;    // Token being bought  
        address maker;         // Order creator
        address receiver;      // Funds recipient
        address allowedSender; // Who can fill order
        uint256 makingAmount;  // Amount being sold
        uint256 takingAmount;  // Amount being bought
        uint256 offsets;       // Bit-packed order data
        bytes interactions;    // Custom logic
    }

    function fillOrderTo(
        address order,
        uint256 value,
        uint256 makingAmount,
        uint256 takingAmount,
        bytes calldata interactionData
    ) external returns (uint256, uint256);
}

interface IOrderMixin {
    /**
     * @dev Fill a limit order
     */
    function fillOrder(
        IFusionOrder.Order calldata order,
        bytes calldata signature,
        bytes calldata interaction,
        uint256 makingAmount,
        uint256 takingAmount
    ) external returns (uint256 actualMakingAmount, uint256 actualTakingAmount);

    /**
     * @dev Cancel order
     */
    function cancelOrder(IFusionOrder.Order calldata order) external;

    /**
     * @dev Check if order is valid
     */
    function isValidOrder(
        IFusionOrder.Order calldata order,
        bytes calldata signature
    ) external view returns (bool);
}

interface IFusionResolver {
    /**
     * @dev Resolve Fusion+ order with custom logic
     */
    function resolveOrder(
        IFusionOrder.Order calldata order,
        bytes calldata signature,
        bytes calldata resolverData
    ) external;

    /**
     * @dev Check if resolver can handle order
     */
    function canResolve(
        IFusionOrder.Order calldata order
    ) external view returns (bool);
}

/**
 * @dev Events for tracking
 */
interface IFusionEvents {
    event OrderFilled(
        bytes32 indexed orderHash,
        uint256 makingAmount,
        uint256 takingAmount
    );

    event OrderCancelled(bytes32 indexed orderHash);

    event OrderResolved(
        bytes32 indexed orderHash,
        address indexed resolver,
        bytes resolverData
    );
}

contracts/SUIDutchAuctionResolver.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/I1inch.sol";

/**
 * @title SUIDutchAuctionResolver
 * @dev Custom 1inch Fusion+ resolver for SUI cross-chain auctions
 */
contract SUIDutchAuctionResolver is ReentrancyGuard, Ownable {
    IFusionOrder public immutable settlement;

    struct AuctionOrder {
        address seller;
        uint256 startPrice;
        uint256 endPrice;
        uint256 duration;
        uint256 startTime;
        bool isActive;
        address escrowContract;
        bytes32 secretHash;
    }

    struct AuctionResult {
        address winner;
        uint256 finalPrice;
        string secretHash;
        address escrowContract;
        bytes orderData;
    }

    // Mapping from order hash to auction order details
    mapping(bytes32 => AuctionOrder) public auctionOrders;
    mapping(bytes32 => bool) public resolvedOrders;
    mapping(address => bool) public authorizedOperators;

    // Events
    event FusionOrderFilled(
        bytes32 indexed orderHash,
        address indexed seller,
        uint256 finalPrice,
        uint256 settledAmount
    );

    event AuctionOrderCreated(
        bytes32 indexed orderHash,
        address indexed seller,
        uint256 startPrice,
        uint256 endPrice
    );

    event OrderResolved(
        bytes32 indexed orderId,
        address indexed winner,
        uint256 finalPrice,
        string secretHash
    );

    event AuctionSettled(
        address indexed escrow,
        address indexed winner,
        uint256 amount
    );

    modifier onlyAuthorized() {
        require(
            authorizedOperators[msg.sender] || msg.sender == owner(),
            "Not authorized"
        );
        _;
    }

    constructor(address _settlement) Ownable(msg.sender) {
        require(_settlement != address(0), "Invalid settlement");
        settlement = IFusionOrder(_settlement);
        authorizedOperators[msg.sender] = true;
    }

    /**
     * @dev Create auction order
     */
    function createAuctionOrder(
        bytes32 orderHash,
        address seller,
        uint256 startPrice,
        uint256 endPrice,
        uint256 duration,
        address escrowContract,
        bytes32 secretHash
    ) external onlyAuthorized {
        require(orderHash != bytes32(0), "Invalid order hash");
        require(seller != address(0), "Invalid seller");
        require(startPrice > endPrice, "Invalid price range");
        require(escrowContract != address(0), "Invalid escrow");

        auctionOrders[orderHash] = AuctionOrder({
            seller: seller,
            startPrice: startPrice,
            endPrice: endPrice,
            duration: duration,
            startTime: block.timestamp,
            isActive: true,
            escrowContract: escrowContract,
            secretHash: secretHash
        });

        emit AuctionOrderCreated(orderHash, seller, startPrice, endPrice);
    }

    /**
     * @dev Main resolver function for 1inch Fusion+ integration
     * @param order The 1inch order to resolve
     * @param auctionResult Results from SUI auction
     */
    function resolveOrder(
        IFusionOrder.Order calldata order,
        bytes calldata /* signature */,
        AuctionResult calldata auctionResult
    ) external nonReentrant onlyAuthorized {

        bytes32 orderId = keccak256(abi.encode(order));
        require(!resolvedOrders[orderId], "Order already resolved");

        // Mark as resolved first (CEI pattern)
        resolvedOrders[orderId] = true;

        // Validate auction result
        require(auctionResult.winner != address(0), "Invalid winner");
        require(auctionResult.finalPrice > 0, "Invalid price");
        require(auctionResult.escrowContract != address(0), "Invalid escrow");

        // Execute 1inch order settlement
        _settleFusionOrder(order, auctionResult);

        // Release funds from escrow using secret
        _releaseEscrow(auctionResult);

        emit OrderResolved(
            orderId,
            auctionResult.winner,
            auctionResult.finalPrice,
            auctionResult.secretHash
        );
    }

    /**
     * @dev Settle the 1inch Fusion+ order
     */
    function _settleFusionOrder(
        IFusionOrder.Order calldata order,
        AuctionResult calldata auctionResult
    ) internal {

        // Transfer tokens to winner based on auction result
        IERC20 token = IERC20(order.makerAsset);
        require(
            token.transferFrom(
                order.maker,
                auctionResult.winner,
                auctionResult.finalPrice
            ),
            "Token transfer failed"
        );

        emit AuctionSettled(
            auctionResult.escrowContract,
            auctionResult.winner,
            auctionResult.finalPrice
        );
    }

    /**
     * @dev Release funds from escrow contract
     */
    function _releaseEscrow(AuctionResult calldata auctionResult) internal {

        // Call escrow contract to reveal secret and release funds
        (bool success, ) = auctionResult.escrowContract.call(
            abi.encodeWithSignature(
                "revealAndRelease(string,address)",
                auctionResult.secretHash,
                auctionResult.winner
            )
        );

        require(success, "Escrow release failed");
    }

    /**
     * @dev Handle Fusion settlement (legacy method)
     */
    function _handleFusionSettlement(
        bytes32 orderHash,
        bytes calldata orderData,
        uint256 finalPrice
    ) internal {
        require(orderHash != bytes32(0), "Invalid order hash");
        require(orderData.length > 0, "Invalid order data");
        require(finalPrice > 0, "Invalid price");

        // Call 1inch Fusion settlement contract
        settlement.fillOrderTo(
            address(uint160(uint256(orderHash))),
            finalPrice,
            finalPrice,
            finalPrice,
            orderData
        );

        emit FusionOrderFilled(
            orderHash,
            auctionOrders[orderHash].seller,
            finalPrice,
            finalPrice
        );
    }

    /**
     * @dev Add authorized operator
     */
    function addOperator(address operator) external onlyOwner {
        authorizedOperators[operator] = true;
    }

    /**
     * @dev Remove authorized operator  
     */
    function removeOperator(address operator) external onlyOwner {
        authorizedOperators[operator] = false;
    }

    /**
     * @dev Emergency function to handle failed orders
     */
    function emergencyResolve(bytes32 orderId) external onlyOwner {
        resolvedOrders[orderId] = true;
    }

    /**
     * @dev Check if order is already resolved
     */
    function isOrderResolved(bytes32 orderId) external view returns (bool) {
        return resolvedOrders[orderId];
    }

    /**
     * @dev Get auction order details
     */
    function getAuctionOrder(bytes32 orderHash) external view returns (
        address seller,
        uint256 startPrice,
        uint256 endPrice,
        uint256 duration,
        uint256 startTime,
        bool isActive,
        address escrowContract,
        bytes32 secretHash
    ) {
        AuctionOrder memory auction = auctionOrders[orderHash];
        return (
            auction.seller,
            auction.startPrice,
            auction.endPrice,
            auction.duration,
            auction.startTime,
            auction.isActive,
            auction.escrowContract,
            auction.secretHash
        );
    }

    /**
     * @dev Check if order can be resolved
     */
    function canResolveOrder(bytes32 orderHash) external view returns (bool) {
        AuctionOrder memory auction = auctionOrders[orderHash];
        return auction.isActive && 
               !resolvedOrders[orderHash] &&
               block.timestamp < auction.startTime + auction.duration;
    }

    /**
     * @dev Get current price for Dutch auction
     */
    function getCurrentPrice(bytes32 orderHash) external view returns (uint256) {
        AuctionOrder memory auction = auctionOrders[orderHash];

        if (!auction.isActive || resolvedOrders[orderHash]) {
            return 0;
        }

        uint256 elapsed = block.timestamp - auction.startTime;

        if (elapsed >= auction.duration) {
            return auction.endPrice;
        }

        // Linear price decay
        uint256 priceReduction = (auction.startPrice - auction.endPrice) * elapsed / auction.duration;
        return auction.startPrice - priceReduction;
    }

    /**
     * @dev Get order status
     */
    function getOrderStatus(bytes32 orderHash) external view returns (
        bool isActive,
        bool isResolved,
        address winner,
        uint256 finalPrice,
        uint256 currentPrice,
        uint256 timeRemaining
    ) {
        AuctionOrder memory auction = auctionOrders[orderHash];

        isActive = auction.isActive;
        isResolved = resolvedOrders[orderHash];
        winner = address(0); // Would need to track winner separately
        finalPrice = 0; // Would need to track final price separately  
        currentPrice = this.getCurrentPrice(orderHash);

        uint256 endTime = auction.startTime + auction.duration;
        timeRemaining = block.timestamp < endTime ? endTime - block.timestamp : 0;
    }
}

